import { useState } from "react";
import { motion } from "framer-motion";
import { useInView } from "react-intersection-observer";


const Projects = () => {
    const [isDisplayed1, setIsDisplayed1] = useState(false);
    const [isDisplayed2, setIsDisplayed2] = useState(false);
    const [isDisplayed3, setIsDisplayed3] = useState(false);
    const [isDisplayed4, setIsDisplayed4] = useState(false);

    const [ref1, inView1] = useInView({
        triggerOnce: true,
        rootMargin: "-100px 0px 0px 0px"
    });
    const [ref2, inView2] = useInView({
        triggerOnce: true,
        rootMargin: "-100px 0px 0px 0px"
    });
    const [ref3, inView3] = useInView({
        triggerOnce: true,
        rootMargin: "-100px 0px 0px 0px"
    });
    const [ref4, inView4] = useInView({
        triggerOnce: true,
        rootMargin: "-100px 0px 0px 0px"
    });

    const handleInView = (ref, setIsDisplayed) => {
        if (inView) {
            setIsDisplayed(true);
        }
    }
    return (
        <div className="projects-page">
            <div className="projects-page-header">
                <h1>Projects</h1>
            </div>
            <div className="projects-page-content">
                <div className="projects-page-content-item">
                    <motion.div
                        ref={ref1}
                        animate={isDisplayed1 ? "visible" : "hidden"}
                        initial="hidden"
                        variants={{
                            visible: {
                                opacity: 1,
                                y: 0,
                                transition: {
                                    delay: 0.5,
                                    duration: 1
                                }
                            },
                            hidden: {
                                opacity: 0,
                                y: -100,
                                transition: {
                                    duration: 1
                                }
                            }
                        }
                        }
                    >
                        <h2>
                            <a href="https://flickrpixelclone.herokuapp.com/" target="_blank" rel="noopener noreferrer">
                                Flickr Pixel Clone
                            </a>
                        </h2>
                        <p>
                            A clone of the Flickr Pixel website.
                        </p>
                    </motion.div>
                </div>
                <div className="projects-page-content-item">
                    <motion.div
                        ref={ref2}
                        animate={isDisplayed2 ? "visible" : "hidden"}
                        initial="hidden"
                        variants={{
                            visible: {
                                opacity: 1,
                                y: 0,
                                transition: {
                                    delay: 0.5,
                                    duration: 1
                                }
                            },
                            hidden: {
                                opacity: 0,
                                y: -100,
                                transition: {
                                    duration: 1
                                }
                            }
                        }
                        }
                    >
                        <h2>
                            <a href="https://who-dis-app.herokuapp.com/" target="_blank" rel="noopener noreferrer">
                                Who Dis App
                            </a>
                        </h2>
                        <p>
                            A clone of the Who Dis App website.
                        </p>
                    </motion.div>
                </div>
                <div className="projects-page-content-item">
                    <motion.div
                        ref={ref3}
                        animate={isDisplayed3 ? "visible" : "hidden"}
                        initial="hidden"
                        variants={{
                            visible: {
                                opacity: 1,
                                y: 0,
                                transition: {
                                    delay: 0.5,
                                    duration: 1
                                }
                            },
                            hidden: {
                                opacity: 0,
                                y: -100,
                                transition: {
                                    duration: 1
                                }
                            }
                        }
                        }
                    >
                        <h2>
                            <a href="https://snack-overflow-premium.herokuapp.com/" target="_blank" rel="noopener noreferrer">
                                Snack Overflow Premium
                            </a>
                        </h2>
                        <p>
                            A clone of the Snack Overflow website.
                        </p>
                    </motion.div>
                </div>
                <div className="projects-page-content-item">
                    <motion.div
                        ref={ref4}
                        animate={isDisplayed4 ? "visible" : "hidden"}
                        initial="hidden"
                        variants={{
                            visible: {
                                opacity: 1,
                                y: 0,
                                transition: {
                                    delay: 0.5,
                                    duration: 1
                                }
                            },
                            hidden: {
                                opacity: 0,
                                y: -100,
                                transition: {
                                    duration: 1
                                }
                            }
                        }
                        }
                    >
                        <h2>
                            <a href="https://facebook-cap.herokuapp.com/" target="_blank" rel="noopener noreferrer">
                                Facebook Cap
                            </a>
                        </h2>
                        <p>
                            A clone of the Facebook Cap website.
                        </p>
                    </motion.div>
                </div>
            </div>
        </div>
    );
}

export default Projects;
