import { motion } from "framer-motion";
import { useInView } from "react-intersection-observer";
import { useSpring, animated } from "react-spring";
import { useWindowSize } from "react-use";
import { useEffect } from "react";

const AboutMePage = () => {
    const [ref, inView] = useInView({
        threshold: 0.5,
        triggerOnce: true,
    });
    const { width } = useWindowSize();
    const [isVisible, setIsVisible] = useState(false);
    const spring = useSpring({
        opacity: isVisible ? 1 : 0,
        transform: isVisible ? "translateY(0)" : "translateY(20px)",
        from: { opacity: 0, transform: "translateY(20px)" },
    });

    useEffect(() => {
        if (inView) {
            setIsVisible(true);
        }
    }, [inView]);

    return (
        <div className="about-page">
            <div className="about-page__header">
                <div className="about-page__header-title">
                    <h1>About Me</h1>
                </div>
                <div className="about-page__header-image">
                    <img src={require("../../assets/images/about-me.jpg")} alt="about-me" />
                </div>
            </div>
            <div className="about-page__content">
                <div className="about-page__content-text">
                    <div className="about-page__content-text-title">
                        <h1>About Me</h1>
                    </div>
                    <div className="about-page__content-text-paragraph">
                        <p>
                            I am a full stack web developer with a passion for creating
                            beautiful and functional websites. I have a background in
                            computer science and a passion for creating websites that
                            are user friendly and aesthetically pleasing.
                        </p>
                        <p>
                            I am currently looking for a position in the web development
                            industry. If you would like to get in touch, please send me an
                            email at{"yems500@gmail.com"}
                            <a href="mailto:yems500@gmail.com">
                                <i className="fas fa-envelope"></i>
                            </a>
                        </p>
                    </div>
                </div>
                <div className="about-page__content-image">
                    <img src={require("../../assets/images/about-me.jpg")} alt="about-me" />
                </div>
            </div>
        </div>
    );
}

export default AboutMePage;
