import { useState, useContext } from "react";
import { MenuContext } from "../../context/MenuContext";
import { Link } from "react-router-dom";

const MenuList = () => {
    const { setMenuText } = useContext(MenuContext);
    const [isProjectHovered, setIsProjectHovered] = useState(false);
    const [isWorkHovered, setIsWorkHovered] = useState(false);
    const [isContactHovered, setIsContactHovered] = useState(false);
    const [isAboutHovered, setIsAboutHovered] = useState(false);
    const [isSkillHovered, setIsSkillHovered] = useState(false);

    return (
        <>
            <ul className="menuList">
                <li
                    onMouseEnter={() => {
                        setIsProjectHovered(true);
                        setIsSkillHovered(false);
                        setIsAboutHovered(false);
                        setMenuText("projects");
                    }}
                >
                    <Link to="/projects">Projects</Link>


                    <div
                        className={
                            isProjectHovered ? "redIndicator" : "redIndicatorInvisible"
                        }
                    ></div>
                </li>
                <li
                    onMouseEnter={() => {
                        setIsProjectHovered(false);
                        setIsSkillHovered(true);
                        setIsAboutHovered(false);
                        setMenuText("skills");
                    }}
                >
                    <Link to="/skills">Skills</Link>
                    ) : (
                    <Link to="/skills">Comp√©tences</Link>
                    )}
                    <div
                        className={isSkillsHovered ? "redIndicator" : "redIndicatorInvisible"}
                    ></div>
                </li>
                <li
                    onMouseEnter={() => {
                        setIsProjectsHovered(false);
                        setIsSkillsHovered(false);
                        setIsAboutHovered(true);
                        setIsLanguageHovered(false);
                        setMenuText("about");
                    }}
                >
                    {selectedLanguage === "english" ? (
                        <Link to="/about">About me</Link>
                    ) : (
                        <Link to="/about">A propos</Link>
                    )}

                    <div
                        className={isAboutHovered ? "redIndicator" : "redIndicatorInvisible"}
                    ></div>
                </li>
            </ul>
        </>
    );
}


export default MenuList;
